    this.extend('utilities/MutableBase');
    this.implement('dashboard/UIComponent');
};

exports.setup = function(){
    this.input("userCommand", {
        "type": "JSON"
    });
    this.output("accessor");
    this.output("parking");
};

exports.initialize = function(){
    this.addInputHandler("userCommand", function(){
        var command = this.get('userCommand');

        if(command && command.message && command.message.id){
            switch(command.message.id){
                case "parking":
                    this.send("parking", rawIn);
                    break;
                case "system":
                    this.send("accessor", rawIn);
                    break;
                default:
                    error("Received message in router with an invalid ID");
            }
        } else {
            error("Received message in router and was unable to identify ID");
        }
    });
};

